import{o as t,g as a,j as e}from"./motion.811a50cf.js";const l='{"title":"Быки и коровы","description":"","frontmatter":{"title":"Быки и коровы","tags":"games"},"relativePath":"games/bulls.md","lastUpdated":1631215690104}',s={},n=[e("p",null,"Игра для двоих: один участник загадывает слово, причем заранее оговарива­ется, сколько в нем должно быть букв (обычно 4-5). Задача второго — отгадать это слово, называя другие четырех- или пятибуквенные слова; если какие-то буквы названного слова есть в загаданном, они называются коровами, а если у них совпадает и место внутри слова, то это быки.",-1),e("p",null,"Представим, что загадано слово «чудак». Если отгадывающий говорит «точка», то получает от второго игрока ответ: «три коровы» (то есть буквы «ч», «к» и «а», которые есть и в «чу­даке», и в «точке», но в разных местах). Если затем он скажет «кочан», то полу­чит уже не трех коров, а двух коров и одного быка — поскольку буква «а» и в «чудаке», и в «кочане» находится на четвертой позиции. В итоге рано или поздно удается угадать слово, и игроки могут поменяться местами: теперь первый будет загадывать слово и подсчитывать быков с коровами, а второй — называть свои варианты и отслеживать, в какой степени они совпадают с загаданным. Можно и усложнить процесс, одновременно и загадывая свое слово, и угадывая слово противника.",-1)];s.render=function(e,l,s,r,o,i){return t(),a("div",null,n)};export{l as __pageData,s as default};
