import{o as t,g as a,j as e}from"./motion.811a50cf.js";const n='{"title":"Цепочка слов","description":"","frontmatter":{"title":"Цепочка слов","tags":"games"},"relativePath":"games/chain.md","lastUpdated":1631207317360}',r={},s=[e("p",null,"Игра для любого количества игроков. Многие знают ее под названием «Как сделать из мухи слона», а придумал ее писатель и математик Льюис Кэрролл, автор «Алисы». «Цепочка» основана на словах-метаграммах, то есть словах, различающихся лишь одной буквой. Задача игроков — превратить одно слово в другое с наименьшим количеством промежуточных звеньев. К примеру, сделаем из «лисы» «козу»: ЛИСА — ЛИПА — ЛАПА — КАПА — КАРА — КОРА — КОЗА. Интересно давать задания с сюжетом: чтобы «день» превратился в «ночь», «река» стала «морем». Известная цепочка, где «слон» вырастает из «мухи», получается за 16 ходов: МУХА — МУРА — ТУРА — ТАРА — КАРА — КАРЕ — КАФЕ — КАФР — КАЮР — КАЮК — КРЮК — УРЮК — УРОК — СРОК — СТОК — СТОН — СЛОН (пример Евгения Гика). Для тренировки можно посоревноваться в поиске метаграмм для какого-либо слова. К примеру, слово «тон» дает «сон», «фон», «ток», «том», «тан» и так далее — кто наберет больше вариантов, тот и выиграл.",-1)];r.render=function(e,n,r,i,o,l){return t(),a("div",null,s)};export{n as __pageData,r as default};
