import{o as t,g as a,j as e}from"./motion.811a50cf.js";const s='{"title":"Диссоциации","description":"","frontmatter":{"title":"Диссоциации","tags":"games"},"relativePath":"games/diss.md","lastUpdated":1631207325650}',r={},i=[e("p",null,"Игроки по очереди называют слова, не связанные ассоциативно с предыдущим названным словом. Все остальные следят, чтобы этой связи не было. Если игрок называет слово, явно ассоциирующееся с предыдущим или если обозначаемый им предмет находится в поле зрения игрока, то игрок получает штрафной балл — «укор» — и должен придумать новое слово для начала следующего раунда. По мере возможности игроки стараются ускорять называние слов. Также все стараются отслеживать стратегию придумывания слов друг друга. Это может быть любое правило или принцип отбора: слова на одну и ту же букву, названия растений, части какого-то механизма и любые другие закономерности в ответах. После третьего применения кем-то одной и той же стратегии любой игрок может указать такому игроку на эту стратегию и, при поддержке большинства остальных участников, запрещает её использование до конца игры. Таким образом игроки все усложняют возможности мыслить ассоциативно и постепенно учатся обращаться к необусловленной ничем фантазии, порождающей уникальные идеи.",-1)];r.render=function(e,s,r,n,o,l){return t(),a("div",null,i)};export{s as __pageData,r as default};
