import{o as t,g as a,j as e}from"./motion.811a50cf.js";const r='{"title":"Шляпа","description":"","frontmatter":{"title":"Шляпа","tags":"games"},"relativePath":"games/hat.md","lastUpdated":1631207331748}',s={},n=[e("p",null,"Игра для компании от четырех человек, требующая нехитрого инвентаря: ручек, бумаги и «шляпы» (подойдет и обыкновенный целлофановый пакет). Листы бумаги нужно разорвать на небольшие кусочки и раздать игрокам, количество кусочков зависит от того, сколько людей играет: чем больше компания, тем меньше приходится на каждого. Игроки пишут на бумажках слова (по одному на каждый кусочек бумаги) и бросают в «шляпу». Тут тоже есть варианты — можно играть просто со словами (существительное, нари­цательное, единственного числа), а можно с известными людьми или литера­турными персонажами. Затем участники делятся на команды — по два или больше человек в каждой; задача каждого — за 20 секунд (или 30, или мину­ту — хронометраж можно установить по собственному выбору) объяснить своим партнерам по команде наибольшее количество слов, произвольно вытащенных из «шляпы», не используя при этом однокоренные. Если водящий не смог объяснить какое-то слово, оно возвращается в шляпу и будет разыгра­но другой командой. В конце игры слова, отгаданные разными представителя­ми одной и той же команды, суммируются, их количество подсчитывается, и команде, у которой окажется больше бумажек, присуждается победа. Попу­лярный вариант игры: все то же самое, но в первый кон игроки объясняют слова (или описывают персонажей) устно, во второй — показывают панто­мимой, в третий — объясняют те же слова одним словом. А недавно появилась и настольная игра, где нужно не только объяснять и показывать, но и рисовать.",-1)];s.render=function(e,r,s,o,i,l){return t(),a("div",null,n)};export{r as __pageData,s as default};
