import{o as t,g as a,j as e}from"./motion.811a50cf.js";const r='{"title":"Игра из «Бесславных ублюдков»","description":"","frontmatter":{"title":"Игра из «Бесславных ублюдков»","tags":"games"},"relativePath":"games/inglour.md","lastUpdated":1631207341243}',n={},o=[e("p",null,"Игра для компании любого размера, которую многие знали и до фильма Квен­тина Тарантино, но единого названия у нее нет.Каждый игрок придумывает для своего соседа роль (обычно это какой-нибудь известный человек), пишет ее на бумажке и приклеивает бумажку соседу на лоб: соответственно, все ви­дят, какая у кого роль, но не знают, кто же они сами. Задача участников — с помощью наводящих вопросов, ответы на которые формулируются как «да» или «нет» («Я историческая личность?», «Я деятель культуры?», «Я знамени­тый спортсмен?»), узнать, кем именно они являются. В такой форме, впрочем, игра довольно быстро себя исчерпывает, поэтому темы можно придумывать совершенно разные и вместо известных людей играть, например, в профессии (включая экзотические — «карусельщик», «таксидермист»), в кино- и литера­турных героев (можно смешивать их с реальными знаменитостями, но об этом лучше договориться заранее), в еду (один игрок будет ризотто, а другой, скажем, зелеными щами) и даже просто в предметы.",-1)];n.render=function(e,r,n,s,i,l){return t(),a("div",null,o)};export{r as __pageData,n as default};
