import{o as t,g as a,j as e}from"./motion.811a50cf.js";const l='{"title":"Игра из «Бесславных ублюдков»","description":"","frontmatter":{"title":"Игра из «Бесславных ублюдков»","tags":"games"},"relativePath":"games/inglour.md","lastUpdated":1631215783638}',n={},r=[e("p",null,"Игра для компании любого размера, которую многие знали и до фильма Квен­тина Тарантино, но единого названия у нее нет. Каждый игрок придумывает для своего соседа роль (обычно это какой-нибудь известный человек), пишет ее на бумажке и приклеивает бумажку соседу на лоб: соответственно, все ви­дят, какая у кого роль, но не знают, кто же они сами.",-1),e("p",null,"Задача участников — с помощью наводящих вопросов, ответы на которые формулируются как «да» или «нет» («Я историческая личность?», «Я деятель культуры?», «Я знамени­тый спортсмен?»), узнать, кем именно они являются.",-1),e("p",null,"В такой форме, впрочем, игра довольно быстро себя исчерпывает, поэтому темы можно придумывать совершенно разные и вместо известных людей играть, например, в профессии (включая экзотические — «карусельщик», «таксидермист»), в кино- и литера­турных героев (можно смешивать их с реальными знаменитостями, но об этом лучше договориться заранее), в еду (один игрок будет ризотто, а другой, скажем, зелеными щами) и даже просто в предметы.",-1)];n.render=function(e,l,n,o,s,i){return t(),a("div",null,r)};export{l as __pageData,n as default};
