import{o as t,g as l,j as e}from"./motion.811a50cf.js";const n='{"title":"Интеллект","description":"","frontmatter":{"title":"Интеллект","tags":"games"},"relativePath":"games/intel.md","lastUpdated":1631215803741}',a={},r=[e("p",null,"Письменная игра для компании (но можно играть и вдвоем), состоящая из трех туров, каждый по пять минут. В первом игроки случайным образом набирают тринадцать букв (например, вслепую тыкая пальцем в книжную страницу) и затем составляют из них слова, причем только длинные — от пяти букв.",-1),e("p",null,"Во втором туре требуется выбрать слог и вспомнить как можно больше слов, которые на него начинаются, можно использовать однокоренные (например, если выбран слог «дом», то подойдут слова «дом», «домра», «домна», «домен», «домовой», «домохозяйка» и т. п.). Наконец, в третьем туре вновь берется слог, но теперь нужно вспоминать не обыкновенные слова, а фамилии известных людей прошлого и настоящего, в которых он фигурирует, причем не обяза­тельно в начале, — то есть на слог «кар» подойдут и Карамзин, и Маккартни, и, например, Гамилькар.",-1),e("p",null,"Важная деталь: так как этот кон провоцирует больше всего споров и жульничества, участники игры могут просить друг друга дока­зать, что этот человек действительно знаменитость, и тут нужно вспомнить хотя бы профессию и страну. Типичный диалог: «Как, вы не знаете Гамиль­кара? Но это же карфагенский полководец!»",-1),e("p",null,"После каждого тура подсчиты­ваются очки: если то или иное слово совпало у всех играющих, оно просто вычеркивается, в остальных же случаях игрокам присуждается за него столько очков, сколько соперников не смогли его вспомнить. В первом туре можно еще добавлять очки за особенно длинные слова.",-1),e("p",null,"По итогам туров нужно опреде­лять, кто занял первое, второе, третье и другие места, — и в финале игры эти места сложить. Цель — получить на выходе наименьшее число (к примеру, если вы были победителями всех трех туров, то у вас получится число 3 — 1+1+1, и вы чемпион; меньше не может быть чисто математически).",-1)];a.render=function(e,n,a,s,i,o){return t(),l("div",null,r)};export{n as __pageData,a as default};
