import{o as t,g as e,j as a}from"./motion.811a50cf.js";const n='{"title":"Игра в «П»","description":"","frontmatter":{"title":"Игра в «П»","tags":"games"},"relativePath":"games/p.md","lastUpdated":1631207347869}',o={},r=[a("p",null,"Игра для компании от четырех человек, любопытная вариация на тему «шля­пы», но не требующая никаких специальных аксессуаров. Один игрок загадывает другому слово, которое тот должен объяснить остальным, — но пользоваться он может при этом только словами на букву «п» (любыми, кроме однокоренных). То есть слово «дом» придется объяснять, например, так: «построил — проживаю». Если сразу угадать не удалось, можно подбрасывать дополнительные ассоциации: «постройка, помещение, пространство, простей­шее понятие…» И под конец добавить, например, «Периньон» — по ассоциации с шампанским Dom Perignon. Если отгадывающие близки к победе, то ведуще­му пригодятся комментарии типа «примерно», «приблизительно», «почти пра­вильно» — или, в противоположной ситуации: «плохо, подождите!». Обычно после того, как слово угадано, объяснявший придумывает новое слово и шеп­чет его на ухо угадавшему — тот становится следующим ведущим.",-1)];o.render=function(a,n,o,s,i,l){return t(),e("div",null,r)};export{n as __pageData,o as default};
