import{o as t,g as a,j as e}from"./motion.811a50cf.js";const l='{"title":"Узнай песню","description":"","frontmatter":{"title":"Узнай песню","tags":"games"},"relativePath":"games/song.md","lastUpdated":1631215929777}',n={},s=[e("p",null,"Игра для компании от четырех-пяти человек. Ведущий удаляется, а остав­шиеся игроки выбирают известную песню и распределяют между собой ее слова — каждому по слову.",-1),e("p",null,"Например, загадана песня «Пусть всегда будет солнце»: одному игроку достается слово «пусть», второму — «всегда», тре­тьему — «будет», четвертому — «солнце». Ведущий возращается и начинает задавать вопросы — самые разные и неожиданные: «Твой любимый город?», «Куда впадает Волга?», «Что делать и кто виноват?». Задача отвечающих — употре­бить в ответе свое слово и постараться сделать это так, чтобы оно не слишком выделялось; отвечать нужно быстро и не очень пространно, но не обязательно правдиво.",-1),e("p",null,"Ответами на вопросы в данном случае могут быть, например, «Один город мне выбрать сложно, но пусть сегодня это будет Рио-де-Жанейро» или «Волга — в Каспийское, но так происходит не всегда, каждый третий год она впадает в Черное». Ведущий должен уловить, какое слово в ответе лишнее, и угадать песню. Часто играют со строчками из стихов, а не из песен.",-1)];n.render=function(e,l,n,o,r,i){return t(),a("div",null,s)};export{l as __pageData,n as default};
