import{o as t,g as e,j as a}from"./motion.811a50cf.js";const r='{"title":"Телеграммы","description":"","frontmatter":{"title":"Телеграммы","tags":"games"},"relativePath":"games/telegrams.md","lastUpdated":1631207360282}',s={},l=[a("p",null,"Игра для любого количества игроков. Играющие выбирают слово, на каждую букву которого нужно будет придумать часть телеграммы — первая буква ста­нет началом первого слова, вторая — второго и так далее. К примеру, выбрано слово «вилка». Тогда телеграммой может стать следующее послание: «Верблюд исцелен. Лечу крокодила. Айболит». Еще один виток игры — добавление жан­ров. Каждый игрок получает задание написать не одну, а несколько телеграмм из одного и того же слова — деловую, поздравительную, романтическую (о ти­пах сообщений договариваются заранее). Телеграммы зачитываются вслух, выбирается следующее слово.",-1)];s.render=function(a,r,s,n,o,i){return t(),e("div",null,l)};export{r as __pageData,s as default};
