import{o as t,g as e,j as a}from"./motion.811a50cf.js";const l='{"title":"Телеграммы","description":"","frontmatter":{"title":"Телеграммы","tags":"games"},"relativePath":"games/telegrams.md","lastUpdated":1631215936810}',r={},s=[a("p",null,"Игра для любого количества игроков. Играющие выбирают слово, на каждую букву которого нужно будет придумать часть телеграммы — первая буква ста­нет началом первого слова, вторая — второго и так далее. К примеру, выбрано слово «вилка». Тогда телеграммой может стать следующее послание: «Верблюд исцелен. Лечу крокодила. Айболит».",-1),a("p",null,"Еще один виток игры — добавление жан­ров. Каждый игрок получает задание написать не одну, а несколько телеграмм из одного и того же слова — деловую, поздравительную, романтическую (о ти­пах сообщений договариваются заранее). Телеграммы зачитываются вслух, выбирается следующее слово.",-1)];r.render=function(a,l,r,n,o,i){return t(),e("div",null,s)};export{l as __pageData,r as default};
